mapper.select:
SELECT id, report_id reportId, device_name deviceName, device_id deviceId, repair_comment repairComment, replace_parts replaceParts, validate_method validateMethod, calibrated_volume calibratedVolume, repair_user_name repairUserName, repair_status repairStatus, repair_car_no repairCarNo FROM t_report_repair WHERE delete_status = 1
AND id = #{id}
AND report_id = #{reportId}

SELECT rr.id, report_id reportId, device_id deviceId, device_name deviceName, repair_comment repairComment, replace_parts replaceParts, validate_method validateMethod, calibrated_volume calibratedVolume, repair_status repairStatus, repair_car_no repairCarNo, repair_user_name repairUserName FROM t_report_repair rr JOIN t_report_info ri ON rr.report_id = ri.id WHERE ri.delete_status = 1
AND ri.report_status=#{reportStatus}
AND rr.device_id = #{deviceId}
AND ri.create_time > #{startTime}
AND ri.create_time < #{endTime}
